import Head from 'next/head'
import Image from 'next/image'
import Link from 'next/link'

import Date from '../../../components/Date'
import Tags from '../../../components/Tags'

import { getSortedPostsData } from '../../../lib/posts'

import { DisplaysPosts, DisplaysDescription} from '../../../styles/blog'

export default function TagsReact({ tags }) {
  return (
    <div>
      <Head>
        <title>Felipe Ferreira</title>
        <meta name="description" content="Generated by create next app" />
        <link rel="icon" href="/Component.svg" />
      </Head>

      <main>
        <Tags />

        <div>
          {tags.map((post, index) => (
            <DisplaysPosts key={index}>
              <div>
                <img
                  src={post.thumbnailUrl}
                  alt="thumbnail"
                />
              </div>
              <DisplaysDescription>
                <Link href={'/blog/' + post.slug}>
                  <h2>{post.title}</h2>
                </Link>
                  <div>
                    <Date dateString={post.date} />
                    <p>üïê{post.min}</p>
                    <p>üìå{post.tags}</p>
                  </div>
                  <p>{post.description}</p>
              </DisplaysDescription>
            </DisplaysPosts>
          ))}
        </div>
      </main>
    </div>
  )
}

export async function getStaticProps() {
  const allPostsData = getSortedPostsData()
  const tags = allPostsData.filter(i => i.tags === 'react')
  return {
    props: {
      tags
    }
  }
}
